package org.mappinganalysis.model;import org.apache.flink.hadoop.shaded.com.google.common.collect.Lists;import java.util.List;import java.util.Map;/** * Helper class. */public class PropertyHelper {  /**   * Add a single value to an existing properties object with key value entries as well as multi-valued   * key value pairs as lists.   * @param vertexProperties existing properties   * @param value new value   * @param key key   * @return resulting properties   */  public static Map<String, Object> addValueToProperties(Map<String, Object> vertexProperties, Object value, String key) {    if (vertexProperties.containsKey(key)) {      Object oldValue = vertexProperties.get(key);      if (oldValue instanceof List) {        List<Object> values = Lists.newArrayList((List<Object>) oldValue);        values.add(value);        vertexProperties.put(key, values);      } else {        vertexProperties.put(key, Lists.newArrayList(oldValue, value));      }    } else {      vertexProperties.put(key, value);    }    return vertexProperties;  }}